INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (37691, 'Clozapine', 'Azithromycin', 'Major', 'Clozapine has the potential to prolong QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can cause QT prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clozapine treatment alone has been associated with ventricular arrhythmia, torsade de pointes, cardiac arrest, and sudden death.', 'DDInter', 'Caution is recommended if clozapine is used in combination with other drugs that can prolong the QT interval. Serum electrolytes, including potassium, magnesium and calcium, should be measured at baseline and periodically during treatment, and any abnormalities corrected prior to initiating clozapine.', 'Synergism', 'Caution is recommended if clozapine is used in combination with other drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37747/', '', 'Tinidazole, Tetracycline, Cefepime, Secnidazole, Cefixime, Amikacin, Tetracycline, Rifamycin, Kanamycin, Oxytetracycline, Netilmicin, More', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (37692, 'Colchicine', 'Azithromycin', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may significantly increase the serum concentrations of colchicine. The mechanism involves enhanced absorption as well as reduced excretion of colchicine due to inhibition of P-gp efflux transporter in the intestine, renal proximal tubule, and liver.', 'DDInter', 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors. For the treatment of acute gout flares, the adjusted dosage recommended is 0.6 mg for one dose. Administration should not be repeated for at least three days.', 'Excretion', 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37748/', '', 'Tetracycline, Norfloxacin, Cefepime, Cefuroxime, Cefixime, Amikacin, Levofloxacin, Ofloxacin, Gatifloxacin, Rifamycin, Kanamycin, More', 'Lesinurad, Febuxostat, Pegloticase, Sulfinpyrazone, Rasburicase, Probenecid', 1767369485);
