INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (37846, 'Azithromycin', 'Pimozide', 'Major', 'Coadministration with the ketolide, telithromycin, as well as certain macrolide antibiotics may increase the plasma concentrations of pimozide. The mechanism is inhibition of pimozide metabolism via CYP450 3A4. High plasma levels of pimozide have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death. Macrolides that may significantly inhibit CYP450 3A4 include clarithromycin, erythromycin, and troleandomycin.', 'DDInter', 'The use of pimozide with telithromycin or macrolide antibiotics is considered contraindicated by the manufacturers, although azithromycin and dirithromycin are generally believed to have little, if any, effect on CYP450 3A4.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37902/', '', 'Tinidazole, Tetracycline, Cefepime, Cefuroxime, Secnidazole, Cefixime, Amikacin, Rifamycin, Kanamycin, Ampicillin, Acyclovir, More', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 1767369485);
