INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (38022, 'Tranylcypromine', 'Methylene blue', 'Major', 'Coadministration of methylene blue with serotonergic agents may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Current research suggests that methylene blue has structural properties similar to monoamine oxidase inhibitors (MAOIs). As such, it may enhance serotonergic effects by inhibiting serotonin metabolism.', 'DDInter', 'Serotonergic agents should not be used in patients receiving methylene blue intravenously. Most serotonergic psychiatric drugs should be stopped 1 to 2 weeks (i.e., 4 to 5 half-lives) prior to treatment with methylene blue if possible, while others such as fluoxetine may require discontinuation up to 5 weeks in advance due to its prolonged half-life.', 'Synergism', 'Serotonergic agents should not be used in patients receiving methylene blue intravenously.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38078/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Cobicistat, Deferoxamine, Levoleucovorin, Arginine, More', '', 1767369485);
