INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (38968, 'Dexamethasone', 'Binimetinib', 'Moderate', 'Coadministration with inducers of CYP450 1A2, P-glycoprotein (P-gp), and/or uridine diphosphate glucuronosyltransferase (UGT) 1A1 or 2B7 may decrease the plasma levels and therapeutic efficacy of binimetinib. The proposed mechanism involves enhanced metabolic clearance of binimetinib through induction of CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7.', 'DDInter', 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7 inducers. Pharmacologic response to binimetinib should be monitored more closely whenever a CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7 inducer is added to or withdrawn from therapy, and the binimetinib dosage adjusted as necessary.', 'Metabolism', 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39024/', '', 'Flunisolide, Betamethasone, Phenylephrine, Triamcinolone, Azelastine, Ciclesonide, Mupirocin, Hydrocortisone, Ipratropium, Cromoglicic acid, Prednisolone, More', 'Lenvatinib, Trametinib, Dacomitinib, Tivozanib, Alectinib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (38969, 'Dexamethasone', 'Bisacodyl', 'Moderate', 'The overuse or abuse of laxatives can cause significant loss of electrolytes and potentiate the risk of hypokalemia associated with corticosteroid therapy. Corticosteroids promote the retention of sodium and water and the excretion of potassium. Although these effects are primarily associated with mineralocorticoids like fludrocortisone, they may also occur with higher dosages of glucocorticoids or adrenocorticotropic agents, particularly if given systemically for longer than brief periods.', 'DDInter', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant therapy with corticosteroids, particularly if fludrocortisone or large doses of a glucocorticoid or adrenocorticotropic agent is given, patients should be counseled to recognize potential signs and symptoms of hypokalemia such as fatigue, myalgia, and muscle weakness. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Antagonism', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39025/', '', 'Ciclesonide, Mupirocin, Mometasone furoate, Budesonide, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, More', 'Alvimopan, Sodium sulfate, Sorbitol, Methylnaltrexone, Plecanatide, Lubiprostone, Methylcellulose, Prucalopride, Linaclotide, Magnesium carbonate', 1767369485);
