INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39183, 'Irinotecan', 'Dexamethasone', 'Moderate', 'Coadministration with inducers of the CYP450 3A4 isoenzyme may decrease the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. Irinotecan is partially metabolized by CYP450 3A4, and induction of this process results in less of the drug available in the plasma for conversion to SN-38 via carboxylesterases..', 'DDInter', 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers. Pharmacologic response to irinotecan should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the irinotecan dosage adjusted as necessary.', 'Metabolism', 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39239/', '', 'Teniposide, Cabazitaxel', 'Ciclesonide, Mupirocin, Mometasone furoate, Budesonide, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Vitamin A, Ipratropium, More', 1767369485);
