INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39273, 'Dexamethasone', 'Naproxen', 'Moderate', 'The combined use of corticosteroids and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', 'DDInter', 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be considered.', 'Synergism', 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39329/', '', 'Bremelanotide, Black cohosh, Cabergoline, Tolazoline, Dimethyl sulfoxide, Chondroitin sulfate, Esomeprazole, Glucosamine', 'Ciclesonide, Mometasone furoate, Mometasone, Vitamin A, Ephedrine, Ipratropium, Hyaluronic acid, Nedocromil, Phenylephrine, Beclomethasone dipropionate, Mometasone furoate, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39274, 'Dexamethasone', 'Natalizumab', 'Major', 'Concomitant or recent use of immunosuppressant, immunomodulating, or antineoplastic agents in patients treated with natalizumab may increase the risk of infections including progressive multifocal leukoencephalopathy (PML), a severely disabling, potentially fatal opportunistic viral infection of the brain.', 'DDInter', 'The safety and efficacy of natalizumab in combination with immunosuppressant, immunomodulating, antineoplastic or other myelosuppressive agents have not been established. In general, patients receiving chronic therapy with such agents should not be treated with natalizumab due to potentially increased risk of PML and other serious infections. Typical symptoms associated with PML are diverse, progress over days to weeks, and include progressive weakness on one side of the body, clumsiness of limbs, disturbance of vision, and changes in thinking, memory, and orientation leading to confusion and personality changes. Due to the long half-life of natalizumab, immune effects are possible for up to 2 to 3 months following its discontinuation.', 'Synergism', 'The safety and efficacy of natalizumab in combination with immunosuppressant, immunomodulating, antineoplastic or other myelosuppressive agents have not been established.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39330/', '', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Abatacept, Inebilizumab, Ozanimod, Belimumab, Emapalumab, Sutimlimab, More', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', 1767369485);
