INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39324, 'Dexamethasone', 'Pirbuterol', 'Minor', 'Although they are often combined in clinical practice, the concomitant use of beta-2 adrenergic agonists and corticosteroids may result in additive hypokalemic effects. Since beta-2 agonists can sometimes cause QT interval prolongation, the development of hypokalemia may potentiate the risk of ventricular arrhythmias including torsade de pointes. However, clinical data are limited, and the potential significance is unknown. Patients who are receiving systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or systemic corticosteroid therapy may be at a greater risk of developing hypokalemia.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39380/', '', 'Mupirocin, Mometasone furoate, Vitamin A, Hyaluronic acid, Ipratropium, Nedocromil, Mometasone furoate, Fluorometholone, Minocycline, Clindamycin, Vitamin A, More', 'Mometasone furoate, Umeclidinium, Aclidinium, Mometasone, Glycopyrronium, Ipratropium, Olodaterol, Beclomethasone dipropionate', 1767369485);
