INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39522, 'Dabrafenib', 'Conjugated estrogens', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39578/', '', 'Bazedoxifene', 'Entrectinib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Ribociclib, Vandetanib, Cobimetinib, Capmatinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39523, 'Dantrolene', 'Conjugated estrogens', 'Major', 'The use of estrogens may potentiate the risk of hepatotoxicity associated with dantrolene therapy. The mechanism of interaction is unknown. Fatal and non-fatal liver disorders of an idiosyncratic or hypersensitivity nature have been reported during dantrolene use.', 'DDInter', 'Dantrolene should be used with caution in patients, particularly females over 35 years of age, who are receiving concomitant estrogen therapy. Liver function tests (SGOT, SGPT, alkaline phosphatase, total bilirubin) should be performed prior to and during dantrolene therapy at appropriate intervals, and the drug should generally be withheld if significant abnormalities are observed. Patients treated with dantrolene should be advised to promptly contact their physician if they develop signs and symptoms of hepatocellular injury such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. The drug should be discontinued if hepatitis is suspected, since early detection and drug withdrawal will increase the likelihood of reversing the damage. Following resolution of clinical and laboratory abnormalities, reinstitution of dantrolene therapy should be attempted with extreme caution and only if benefit clearly outweighs the risk.', 'Synergism', 'Dantrolene should be used with caution in patients, particularly females over 35 years of age, who are receiving concomitant estrogen therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39579/', '', 'Bazedoxifene', '', 1767369485);
