INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39562, 'Griseofulvin', 'Conjugated estrogens', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39618/', '', 'Diethylstilbestrol, Bazedoxifene', 'Terbinafine, Salicylic acid, Selenium Sulfide, Thiabendazole, Nystatin, Ciclopirox, Terbinafine, Econazole, Flucytosine, Tioconazole, Chlorphenesin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39563, 'Hemin', 'Conjugated estrogens', 'Major', 'Coadministration with drugs that are capable of increasing the activity of delta-aminolevulinic acid synthetase (e.g., estrogens, barbiturates, steroid metabolites, rifampin) may negate the pharmacologic effect of hemin. In the treatment of porphyria, hemin is thought to limit the rate of porphyrin/heme biosynthesis by inhibiting delta-aminolevulinic acid synthetase, thus any induction of this enzyme would be expected to counteract the therapeutic effect of hemin.', 'DDInter', 'Drugs capable of increasing the activity of delta-aminolevulinic acid synthetase should be avoided during hemin therapy.', 'Antagonism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39619/', '', 'Bazedoxifene', 'Betibeglogene autotemcel, Streptokinase, Crizanlizumab, Lanadelumab, Icatibant', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39564, 'Hyaluronidase', 'Conjugated estrogens', 'Minor', 'Larger amounts of hyaluronidase may be required to achieve equivalent dispersing effects if patients are concomitantly receiving large doses of salicylates, cortisone, ACTH, estrogens, or antihistamines. These drugs may render tissues partially resistant to the dispersing action of hyaluronidase.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39620/', '', 'Bazedoxifene', 'Betibeglogene autotemcel, Streptokinase, Crizanlizumab, Lanadelumab, Icatibant', 1767369485);
