INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39699, 'Bumetanide', 'Phenobarbital', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39755/', '', 'Ascorbic acid, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Trichlormethiazide, Ciclopirox, Probenecid, Tioconazole, Brinzolamide, More', 'Suvorexant, Thiopental, Quazepam, Amobarbital, Butalbital, Ramelteon, Scopolamine, Eszopiclone, Tasimelteon, Methohexital, Lemborexant, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39700, 'Buprenorphine', 'Phenobarbital', 'Major', 'Concomitant use of buprenorphine with other central nervous system (CNS) depressants has been associated with increased risk of buprenorphine overdose, severe respiratory depression, coma, and death. Coadministration with potent inducers of CYP450 3A4, including certain anticonvulsants such as carbamazepine, phenobarbital, phenytoin and primidone, may decrease the plasma concentrations of buprenorphine, which is primarily metabolized by the isoenzyme. Reduced efficacy or withdrawal symptoms may occur in patients maintained on buprenorphine.', 'DDInter', 'Extreme caution is advised when prescribing buprenorphine in conjunction with other CNS depressants, or when prescribing buprenorphine to patients who are addicted to opioids and also abusing benzodiazepines or alcohol. Due to potential risk of overdose and death, dependence on sedative-hypnotics such as benzodiazepines or alcohol is considered a relative contraindication for office-based buprenorphine treatment of opioid addiction. Dosage reduction of CNS depressants, buprenorphine, or both should be considered if concomitant use is necessary. Pharmacologic response to buprenorphine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', 'Metabolism, Synergism', 'Extreme caution is advised when prescribing buprenorphine in conjunction with other CNS depressants, or when prescribing buprenorphine to patients who are addicted to opioids and also abusing benzodiazepines or alcohol.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39756/', '', 'Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Acetylsalicylic acid, Naloxone, Ibuprofen, Naltrexone', 'Zopiclone', 1767369485);
