INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (40022, 'Beclomethasone dipropionate', 'Mifepristone', 'Major', 'Mifepristone is a potent antiglucocorticoid and may antagonize the effects of corticosteroids. Pharmacokinetically, mifepristone may increase the plasma concentrations of corticosteroids by inhibiting their metabolism via CYP450 3A4. However, the clinical impact has not been studied. Dexamethasone, specifically, may also decrease the blood levels of mifepristone by inducing its metabolism via CYP450 3A4.', 'DDInter', 'The use of mifepristone is considered contraindicated in patients on long-term corticosteroid therapy. When intended for daily use to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, mifepristone is also contraindicated in those patients who require systemic corticosteroids for serious medical conditions or illnesses, such as immunosuppression after organ transplantation. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism, Antagonism', 'The use of mifepristone is considered contraindicated in patients on long-term corticosteroid therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40078/', '', 'Fluocinolone acetonide, Clobetasol, Fluocinonide, Fluorometholone, Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Epinephrine, Cromoglicic acid, Ipratropium, More', 'Ulipristal, Danazol, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (40023, 'Beclomethasone dipropionate', 'Omacetaxine mepesuccinate', 'Moderate', 'The use of omacetaxine with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Omacetaxine alone may cause severe myelosuppression, neutropenia, lymphopenia, and opportunistic infections. The risk may theoretically increase when coadministered with other hematotoxic therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms such as fever, chills, shortness of breath, fatigue, and any unusual bleeding or bruising.', 'Synergism', 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40079/', '', 'Fluocinolone acetonide, Clobetasol, Fluocinonide, Fluorometholone, Formoterol, Cromoglicic acid, Ipratropium, Isoprenaline, Tiotropium, Terbutaline, Aclidinium, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 1767369485);
