INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (40302, 'Mazindol', 'Lorcaserin', 'Major', 'The efficacy of centrally-acting appetite suppressants in combination with other anorectic agents has not been studied, but available data suggest that combined use may have the potential for serious cardiac problems. Possible contributing factors include use for extended periods of time, higher than recommended dosages, and/or use in combination with other anorectic drugs.', 'DDInter', 'Centrally--acting appetite suppressants should not be used in combination with other anorectic agents, including prescription, over-the-counter, and herbal preparations. In addition, they are not recommended for patients who have used any anorectic agents within the prior year. The potential risk of possible serious adverse effects such as valvular heart disease and pulmonary hypertension should be assessed carefully against the benefit of weight loss, and baseline cardiac evaluation should be considered to detect preexisting valvular heart diseases or pulmonary hypertension prior to initiating treatment. These agents are not recommended for use in patients with known heart murmur or valvular heart disease, severe hypertension, or symptomatic cardiovascular disease including arrhythmias.', 'Synergism', 'Centrally--acting appetite suppressants should not be used in combination with other anorectic agents, including prescription, over-the-counter, and herbal preparations.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40358/', '', 'Ephedrine, Lorcaserin, Orlistat, Naltrexone', 'Dexfenfluramine, Orlistat, Naltrexone, Mazindol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (40303, 'Meperidine', 'Lorcaserin', 'Major', 'Concomitant use of lorcaserin with agents that possess or enhance serotonergic activity such as selective serotonin reuptake inhibitors (SSRIs), selective serotonin-norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), 5-HT1 receptor agonists (triptans), ergot alkaloids, phenylpiperidine opioids, bupropion, dextromethorphan, linezolid, lithium, St. John''s wort, tramadol, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', 'DDInter', 'Caution is advised if lorcaserin must be used in combination with other agents that affect the serotonergic neurotransmitter system. Patients should be closely monitored for symptoms of the serotonin syndrome, which may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'Synergism', 'Caution is advised if lorcaserin must be used in combination with other agents that affect the serotonergic neurotransmitter system.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40359/', '', 'Morphine, Naltrexone, Oxymorphone, Naloxone, Oxycodone, Butorphanol, Acetaminophen, Hydromorphone, Dezocine, Opium, Methadone, More', 'Ephedrine, Diethylpropion', 17