INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41301, 'Guanethidine', 'Benzphetamine', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, may decrease the hypotensive effect of postganglionic adrenergic blocking agents such as guanadrel, guanethidine and rauwolfia alkaloids, which work by depleting catecholamine stores from adrenergic nerve endings. CNS stimulants can have peripheral sympathetic activity, thus they may elevate blood pressure on their own as well as antagonize some of the antiadrenergic effect produced by these hypotensive agents.', 'DDInter', 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants. If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41357/', '', 'Latanoprost, Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Clonidine, Physostigmine, Acetylcholine, Betaxolol, Diclofenamide, Bimatoprost, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41302, 'Haloperidol', 'Benzphetamine', 'Moderate', 'Butyrophenone neuroleptics may antagonize the pharmacologic effects of amphetamine, amphetamine derivatives, and other centrally-acting sympathomimetic agents (i.e., CNS stimulants). Conversely, these agents may diminish the neuroleptic efficacy of butyrophenones. The exact mechanism of interaction is unknown but may involve opposing effects on dopaminergic activity.', 'DDInter', 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with a butyrophenone neuroleptic agent.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41358/', '', 'Thiothixene, Risperidone, Amisulpride, Quetiapine, Lumateperone, Pimozide, Cariprazine, Paliperidone, Lurasidone, Pimavanserin, Clozapine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41303, 'Imipramine', 'Benzphetamine', 'Moderate', 'The use of tricyclic antidepressants in combination with amphetamines or sympathomimetic appetite suppressants may produce additive cardiovascular effects, increasing the risk of hypertension, cardiac arrhythmias, tachycardia, and fever. The mechanism involves additive pharmacodynamic effects resulting from increased norepinephrine release by sympathomimetic agents and inhibition of norepinephrine reuptake by tricyclic antidepressants. A pharmacokinetic interaction is also possible between tricyclic antidepressants and amphetamines, since many of these agents are metabolized by CYP450 2D6. Increased plasma levels of one or both drugs may occur during coadministration.', 'DDInter', 'Close monitoring of cardiovascular status is recommended for patients receiving this combination. Patients should be advised to promptly report symptoms such as fever, headache, or fast or irregular heartbeats.', 'Metabolism, Synergism', 'Close monitoring of cardiovascular status is recommended for patients receiving this combination.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41359/', '', 'Maprotiline, St. John''s Wort, Nefazodone, Oxitriptan, Mirtazapine, Viloxazine, Trazodone, Tryptophan', '', 1767369485);
