INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41349, 'Benzphetamine', 'Metamfetamine', 'Major', 'The efficacy of centrally-acting appetite suppressants in combination with other anorectic agents has not been studied, but available data suggest that combined use may have the potential for serious cardiac problems. Possible contributing factors include use for extended periods of time, higher than recommended dosages, and/or use in combination with other anorectic drugs.', 'DDInter', 'Centrally--acting appetite suppressants should not be used in combination with other anorectic agents, including prescription, over-the-counter, and herbal preparations. In addition, they are not recommended for patients who have used any anorectic agents within the prior year. The potential risk of possible serious adverse effects such as valvular heart disease and pulmonary hypertension should be assessed carefully against the benefit of weight loss, and baseline cardiac evaluation should be considered to detect preexisting valvular heart diseases or pulmonary hypertension prior to initiating treatment. These agents are not recommended for use in patients with known heart murmur or valvular heart disease, severe hypertension, or symptomatic cardiovascular disease including arrhythmias.', 'Synergism', 'Centrally--acting appetite suppressants should not be used in combination with other anorectic agents, including prescription, over-the-counter, and herbal preparations.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41405/', '', 'Amphetamine, Pemoline, Atomoxetine, Lisdexamfetamine, Solriamfetol, Caffeine, Methylphenidate, Dexmethylphenidate, Dextroamphetamine, Armodafinil, Citicoline, Modafinil', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41350, 'Benzphetamine', 'Methdilazine', 'Moderate', 'Phenothiazines may antagonize the pharmacologic effects of amphetamine, amphetamine derivatives, and other centrally-acting sympathomimetic agents (i.e., CNS stimulants). Conversely, these agents may diminish the neuroleptic efficacy of phenothiazines. The exact mechanism of interaction is unknown but may involve opposing effects on dopaminergic activity.', 'DDInter', 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with phenothiazines.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41406/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, More', '', 1767369485);
