INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41395, 'Benzphetamine', 'Sertraline', 'Major', 'Serotonin reuptake inhibitors may potentiate the pharmacologic response to sympathomimetic agents.', 'DDInter', 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs). Close monitoring for enhanced sympathomimetic effects and possible serotonin syndrome is recommended if these agents must be used together.', 'Synergism', 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs).', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41451/', '', 'Maprotiline, St. John''s Wort, Nefazodone, Oxitriptan, Mirtazapine, Viloxazine, Trazodone, Tryptophan', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41396, 'Benzphetamine', 'Sibutramine', 'Major', 'Sibutramine is a potent inhibitor of norepinephrine and serotonin reuptake, and a less potent inhibitor of dopamine reuptake. As such, it may potentiate the pharmacologic response to sympathomimetic agents due to additive or synergistic effects. Although the interaction has not been studied with sibutramine, it has been reported with the selective serotonin reuptake inhibitor, fluoxetine.', 'DDInter', 'The use of sibutramine in combination with amphetamines and other centrally-acting appetite suppressants is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41452/', '', 'Orlistat, Naltrexone', '', 1767369485);
