INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41642, 'Benzatropine', 'Prucalopride', 'Moderate', 'Concomitant administration of anticholinergic agents would be expected to reduce the therapeutic effects of prucalopride. Prucalopride increases bowel motility by stimulating colonic peristalsis (high-amplitude propagating contractions), an action that has been shown to be facilitated by acetylcholine release in isolated gastrointestinal tissues from various animal species.', 'DDInter', 'Monitoring for altered efficacy of prucalopride may be advisable during concomitant use with anticholinergic agents.', 'Absorption', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41698/', '', 'Orphenadrine', 'Alvimopan, Sorbitol, Tegaserod, Naloxegol, Bisacodyl, Naldemedine, Lactulose, Methylnaltrexone, Lactitol, Sodium phosphate, monobasic, Magnesium oxide, More', 1767369485);
