INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41714, 'Ambenonium', 'Betamethasone', 'Moderate', 'Corticosteroids and adrenocorticotropic agents may diminish the therapeutic effects of acetylcholinesterase inhibitors in myasthenia gravis. The mechanism of interaction is unknown. Marked deterioration in muscle strength has been reported in patients with myasthenia gravis shortly after the initiation of corticosteroid therapy, particularly when high dosages were used.', 'DDInter', 'Corticosteroid therapy should be instituted at relatively low dosages (15 to 25 mg/day of prednisone or equivalent) and in a controlled setting in patients with myasthenia gravis. Respiratory support should be available, and the dosage should be increased stepwise as tolerated (approximately 5 mg/day of prednisone or equivalent at 2- to 3-day intervals until marked clinical improvement or a dosage of 50 mg/day is reached). Dose reductions of the acetylcholinesterase inhibitor may be required as symptoms improve, which often may be delayed and gradual.', 'Antagonism', 'Corticosteroid therapy should be instituted at relatively low dosages (15 to 25 mg/day of prednisone or equivalent) and in a controlled setting in patients with myasthenia gravis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41770/', '', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', 'Bethanechol, Pilocarpine, Cevimeline', 1767369485);
