INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41789, 'Dinutuximab', 'Betamethasone', 'Major', 'Coadministration of dinutuximab or dinutuximab beta with corticosteroids may potentiate the risk of infections due to additive immunosuppressive activity. Infections, including sepsis, pneumonia, herpes virus infection, myelitis, and encephalomyelitis as well as hematologic toxicities, including thrombocytopenia and neutropenia, have been reported with both dinutuximab and dinutuximab beta therapy.', 'DDInter', 'The manufacturer of dinutuximab beta recommends that, except for life threatening conditions, concomitant use of dinutuximab beta and corticosteroids should be avoided for 2 weeks prior to the first course of dinutuximab beta and for 1 week after the last course (UK). If concomitant use is required, close clinical and laboratory monitoring for the development of systemic infections and severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Synergism', 'The manufacturer of dinutuximab beta recommends that, except for life threatening conditions, concomitant use of dinutuximab beta and corticosteroids should be avoided for 2 weeks prior to the first course of dinutuximab beta and for 1 week after the last course (UK).', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41845/', '', 'Mupirocin, Mometasone furoate, Epinephrine, Vitamin A, Ephedrine, Olopatadine, Ipratropium, Cromoglicic acid, Hyaluronic acid, Nedocromil, Phenylephrine, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Amivantamab, Rituximab, More', 1767369485);
