INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41798, 'Edrophonium', 'Betamethasone', 'Moderate', 'Corticosteroids and adrenocorticotropic agents may diminish the therapeutic effects of acetylcholinesterase inhibitors in myasthenia gravis. The mechanism of interaction is unknown. Marked deterioration in muscle strength has been reported in patients with myasthenia gravis shortly after the initiation of corticosteroid therapy, particularly when high dosages were used.', 'DDInter', 'Corticosteroid therapy should be instituted at relatively low dosages (15 to 25 mg/day of prednisone or equivalent) and in a controlled setting in patients with myasthenia gravis. Respiratory support should be available, and the dosage should be increased stepwise as tolerated (approximately 5 mg/day of prednisone or equivalent at 2- to 3-day intervals until marked clinical improvement or a dosage of 50 mg/day is reached). Dose reductions of the acetylcholinesterase inhibitor may be required as symptoms improve, which often may be delayed and gradual.', 'Antagonism', 'Corticosteroid therapy should be instituted at relatively low dosages (15 to 25 mg/day of prednisone or equivalent) and in a controlled setting in patients with myasthenia gravis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41854/', '', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', 'Methacholine, Hexaminolevulinate, Secretin human, Methylene blue, Sermorelin, Sorbitol, Galactose, Vitamin A, Caffeine, Metyrapone, Histamine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (41799, 'Efalizumab', 'Betamethasone', 'Moderate', 'The use of efalizumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Efalizumab alone can cause serious infections requiring hospitalization, and the risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated. Patients receiving other immunosuppressive agents should not be treated with efalizumab because of the possibility of excessive immunosuppression. The duration of the period following treatment with efalizumab before one should consider starting other immunosuppressive therapy has not been evaluated.', 'Synergism', 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41855/', '', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, More', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', 1767369485);
