INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (42655, 'Betaxolol', 'Verapamil', 'Major', 'Additive reductions in heart rate, cardiac conduction, and cardiac contractility may occur when calcium channel blockers, especially verapamil and diltiazem, are used concomitantly with beta blockers. While this combination may be useful and effective in some situations, potentially serious cardiovascular adverse effects such as congestive heart failure, severe hypotension, and/or exacerbation of angina may occur. Ventricular asystole, sinus arrest, and heart block have also been reported.', 'DDInter', 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if these agents are used together, and the dosage of one or both agents adjusted as necessary. Patients should be advised to promptly report any symptoms including fatigue, headache, fainting, swelling of the extremities, weight gain, shortness of breath, chest pain, increased or decreased heartbeat, or irregular heartbeat.', 'Synergism', 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if these agents are used together, and the dosage of one or both agents adjusted as necessary.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42717/', '', 'Methazolamide, Neostigmine, Physostigmine, Acetylcholine, Diclofenamide, Brinzolamide, Brimonidine, Levobunolol', 'Enalapril, Moexipril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, Captopril, Fosinopril', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (42656, 'Betaxolol', 'Zaleplon', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42718/', '', 'Latanoprost, Methazolamide, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, Acetazolamide, Brinzolamide, More', 'Suvorexant, Nitrazepam, Thiopental, Amobarbital, Butalbital, Eszopiclone, Ramelteon, Methohexital, Lemborexant, Estazolam, Methylphenobarbital, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (42657, 'Betaxolol', 'Zileuton', 'Moderate', 'Zileuton is believed to inhibit the hepatic metabolism of propranolol. Propranolol serum concentrations may increase by as much as 100% with a resultant increase in beta blockade.', 'DDInter', 'It is unknown if this interaction will occur with other beta-blockers; however, if zileuton and a beta-blocker must be given concomitantly, the patient should be monitored for orthostatic hypotension, cardiac arrhythmias, bradycardia, and heart failure, and beta-blocker dosage should be decreased if necessary.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42719/', '', 'Latanoprost, Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Clonidine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (42658, 'Ziprasidone', 'Betaxolol', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42720/', '', 'Latanoprost, Neostigmine, Pilocarpine, Bimatoprost, Travoprost, Brinzolamide, Brimonidine, Levobunolol', 'Droperidol, Pimozide, Pimavanserin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (42659, 'Betaxolol', 'Zolmitriptan', 'Minor', 'After one week of dosing with zolmitriptan and propranolol 160 mg/day, the Cmax and AUC of zolmitriptan increased 1.5 times while the Cmax and AUC of the N-desmethyl metabolite decreased by 30% and 15%, respectively. The mechanism of the interaction has not been described. It does not appear that any special precautions are necessary when coadministering zolmitriptan with beta-blockers, although further post-marketing studies will help to define this interaction.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42721/', '', 'Latanoprost, Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Clonidine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, More', 'Frovatriptan, Rimegepant, Eptinezumab, Atogepant, Eletriptan, Rizatriptan, Ubrogepant, Naratriptan, Galcanezumab, Sumatriptan, Erenumab, More', 1767369485);
