INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (43200, 'Bexarotene', 'Panobinostat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of panobinostat, which is partially metabolized by the isoenzyme. The interaction has not been evaluated clinically, but simulations using physiologically-based pharmacokinetic (PBPK) models suggest an approximately 70% decrease in panobinostat systemic exposure in the presence of potent CYP450 3A4 inducers.', 'DDInter', 'Caution is advised during concomitant use of panobinostat with CYP450 3A4 inducers. The potential for decreased efficacy of panobinostat should be considered.', 'Metabolism', 'Caution is advised during concomitant use of panobinostat with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43262/', '', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Porfimer sodium, Niraparib, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Altretamine, Tagraxofusp, Enasidenib, Selinexor, Aflibercept, Trastuzumab, Anagrelide, Hydroxyurea, More', 1767369485);
