INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (43320, 'Bexarotene', 'Troglitazone', 'Minor', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of bexarotene. In vitro studies suggest that bexarotene is metabolized by CYP450 3A4. However, concomitant administration with multiple doses of ketoconazole, a potent CYP450 3A4 inhibitor, did not alter bexarotene plasma concentrations, which would imply that bexarotene elimination is not substantially dependent on CYP450 3A4 metabolism in vivo. The effects of concomitant CYP450 3A4 inducers (e.g., carbamazepine, phenytoin, phenobarbital, rifampin) on the pharmacokinetics of bexarotene have not been studied.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43382/', '', 'Guar gum, Acarbose, Pramlintide, Ertugliflozin, Rosuvastatin, Nateglinide, Metformin, Empagliflozin, Canagliflozin, Miglitol, Dapagliflozin', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Enasidenib, Mitotane, Niraparib, Porfimer sodium, Selinexor, Aflibercept, More', 1767369485);
