INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (43596, 'Bicalutamide', 'Sorafenib', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43658/', '', 'Imatinib, Dabrafenib, Dacomitinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Regorafenib, Alpelisib, Erlotinib, Ponatinib, More', 'Anastrozole, Exemestane, Fulvestrant, Megestrol acetate, Ethinylestradiol, Diethylstilbestrol, Medroxyprogesterone acetate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (43597, 'Bicalutamide', 'Sotalol', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43659/', '', 'Nadolol, Labetalol, Propranolol, Esmolol, Betaxolol, Nebivolol, Penbutolol, Bisoprolol, Carteolol, Pindolol, Timolol, More', 'Darolutamide, Anastrozole, Fulvestrant, Letrozole, Aminoglutethimide, Exemestane, Megestrol acetate, Ethinylestradiol, Diethylstilbestrol, Medroxyprogesterone acetate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (43598, 'Bicalutamide', 'Sparfloxacin', 'Major', 'Sparfloxacin may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of sparfloxacin with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43660/', '', 'Darolutamide, Anastrozole, Fulvestrant, Letrozole, Aminoglutethimide, Exemestane, Megestrol acetate, Diethylstilbestrol, Medroxyprogesterone acetate', 'Enoxacin, Nalidixic acid, Cinoxacin, Trovafloxacin, Delafloxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (43599, 'Bicalutamide', 'Stiripentol', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2, 2C19, and/or 3A4 may increase the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways.', 'DDInter', 'Caution is advised when stiripentol is used with CYP450 1A2, 2C19, and/or 3A4 inhibitors. Patients should be monitored for potentially increased adverse effects such as anorexia, nausea, vomiting, weight loss, somnolence, dizziness, confusion, difficulty concentrating, ataxia, hypotonia, tremor, hyperkinesia, dysarthria, suicidal ideation/behaviour, neutropenia, and thrombocytopenia. Dosage adjustments for stiripentol or alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Caution is advised when stiripentol is used with CYP450 1A2, 2C19, and/or 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43661/', '', 'Anastrozole, Fulvestrant, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Exemestane, Diethylstilbestrol, Histrelin, Megestrol acetate, More', 'Brivaracetam, Topiramate, Cenobamate, Lamotrigine, Methylphenobarbital, Phenytoin, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (43600, 'Bicalutamide', 'Sulfamethoxazole', 'Minor', 'Limited data suggest that sulfamethoxazole-trimethoprim (SMX-TMP) may rarely prolong the QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. There have been isolated reports of QT prolongation and ventricular arrhythmias occurring in patients treated with SMX-TMP intravenously. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43662/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Rifapentine, Streptomycin, Trimethoprim, Rifampicin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, More', 'Darolutamide, Anastrozole, Fulvestrant, Letrozole, Aminoglutethimide, Exemestane, Megestrol acetate, Diethylstilbestrol, Medroxyprogesterone acetate', 1767369485);
