INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (43801, 'Bictegravir', 'Troleandomycin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or uridine diphosphate glucuronosyltransferase (UGT) 1A1 may increase the plasma concentrations of bictegravir. According to the product labeling, bictegravir is a substrate of both CYP450 3A4 and UGT1A1; however, the extent to which each enzymatic pathway contributes to the metabolic clearance of bictegravir has not been reported.', 'DDInter', 'Caution is advised when bictegravir is used with CYP450 3A4 and UGT1A1 inhibitors. Patients should be monitored for increased adverse effects such as diarrhea, nausea, and vomiting.', 'Metabolism', 'Caution is advised when bictegravir is used with CYP450 3A4 and UGT1A1 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43863/', '', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, More', 'Dirithromycin, Lincomycin, Clindamycin', 1767369485);
