INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (43810, 'Emtricitabine', 'Zalcitabine', 'Major', 'Concomitant use of the cytidine analog nucleoside reverse transcriptase inhibitors (NRTI) lamivudine and emtricitabine may inhibit the intracellular phosphorylation of one another to their respective active derivative in vivo. This could result in diminished antiretroviral effects of these drugs. However, clinical experience on the coadministration of cytidine analogs is lacking. This interaction may also occur with zalcitabine. In addition, the therapeutic efficacy of these drugs in combination appears limited, since lamivudine and emtricitabine have similar resistance profiles via mutation of the same viral reverse transcriptase gene (M184V).', 'DDInter', 'The use of the cytidine analog NRTIs lamivudine, emtricitabine, or zalcitabine in any combination in an antiretroviral treatment regimen that consists of two NRTIs is not recommended. Local antiretroviral treatment experts should be consulted for current practice.', 'Antagonism', 'The use of the cytidine analog NRTIs lamivudine, emtricitabine, or zalcitabine in any combination in an antiretroviral treatment regimen that consists of two NRTIs is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43872/', '', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', 1767369485);
