INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (45139, 'Bisoprolol', 'Verapamil', 'Major', 'Additive reductions in heart rate, cardiac conduction, and cardiac contractility may occur when calcium channel blockers, especially verapamil and diltiazem, are used concomitantly with beta blockers. While this combination may be useful and effective in some situations, potentially serious cardiovascular adverse effects such as congestive heart failure, severe hypotension, and/or exacerbation of angina may occur. Ventricular asystole, sinus arrest, and heart block have also been reported.', 'DDInter', 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if these agents are used together, and the dosage of one or both agents adjusted as necessary. Patients should be advised to promptly report any symptoms including fatigue, headache, fainting, swelling of the extremities, weight gain, shortness of breath, chest pain, increased or decreased heartbeat, or irregular heartbeat.', 'Synergism', 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if these agents are used together, and the dosage of one or both agents adjusted as necessary.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45209/', '', 'Verapamil', 'Trandolapril, Moexipril, Bisoprolol, Benazepril', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (45140, 'Bisoprolol', 'Zaleplon', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45210/', '', 'Felodipine, Nifedipine, Ivabradine, Felodipine', 'Suvorexant, Nitrazepam, Thiopental, Butalbital, Eszopiclone, Ramelteon, Methohexital, Lemborexant, Estazolam, Methylphenobarbital, Zopiclone, More', 1767369485);
