INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (45192, 'Ginkgo biloba', 'Bivalirudin', 'Moderate', 'Ginkgo may potentiate the risk of bleeding associated with anticoagulants, platelet inhibitors, and thrombolytic agents. Ginkgolide B, a component of ginkgo, inhibits platelet-activating factor by displacing it from its receptor-binding site, resulting in reduced platelet aggregation.', 'DDInter', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In general, consumption of ginkgo should be avoided during use of coagulation-modifying agents and at least two weeks prior to surgery. In patients who have used this herb extensively prior to receiving anticoagulation, antiplatelet or thrombolytic therapy, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45262/', '', 'Desirudin, Protein C, Acenocoumarol, Selexipag, Drotrecogin alfa', 'Rivastigmine, Galantamine, Donepezil, Tacrine, Memantine, Aducanumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (45193, 'Ginseng', 'Bivalirudin', 'Moderate', 'Ginseng may potentiate the effects of anticoagulants, platelet inhibitors and thrombolytic agents, possibly increasing the risk of bleeding. In vitro studies have shown that one or more of the active constituents of ginseng, known as ginsenosides, can decrease platelet aggregation, although it is not known whether these constituents are absorbed or are bioactive in humans. Bleeding complications secondary to antiplatelet activity have not been reported.', 'DDInter', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In general, consumption of ginseng supplements and large amounts of ginseng should preferably be avoided during use of coagulation-modifying agents. In patients who have used this herb extensively prior to receiving anticoagulation, antiplatelet or thrombolytic therapy, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended.', 'Synergism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45263/', '', 'Cilostazol, Iloprost, Treprostinil, Desirudin, Protein C, Acenocoumarol, Selexipag, Epoprostenol, Drotrecogin alfa', '', 1767369485);
