INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (45198, 'Icosapent', 'Bivalirudin', 'Moderate', 'Omega-3 fatty acids (e.g., fish oil) may potentiate the pharmacologic effects of anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, dextran, and nonsteroidal anti-inflammatory drugs (NSAIDs). The exact mechanism of interaction is unknown. Omega-3 fatty acids may possess mild antiplatelet and hypocoagulant activities.', 'DDInter', 'In general, patients should consult a healthcare provider before taking any herbal or nutritional supplements. Patients using omega-3 fatty acids in combination with anticoagulants or other drugs that affect hemostasis should be advised of the potential for increased risk of bleeding complications.', 'Synergism', 'In general, patients should consult a healthcare provider before taking any herbal or nutritional supplements.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45268/', '', 'Protein C, Acenocoumarol, Selexipag', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (45199, 'Iloprost', 'Bivalirudin', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', 'DDInter', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45269/', '', 'Iloprost, Protein C, Acenocoumarol, Selexipag', 'Antithrombin III human, Bivalirudin, Acenocoumarol, Selexipag, Acetylsalicylic acid, Cangrelor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (45200, 'Indomethacin', 'Bivalirudin', 'Moderate', 'The safety and efficacy of bivalirudin have not been established when used in conjunction with platelet inhibitors other than aspirin. Theoretically, the concomitant administration of bivalirudin (plus aspirin) and platelet inhibitors may increase the risk of bleeding complications due to additive or synergistic effects on the clotting cascade. However, limited data from drug interaction studies do not suggest pharmacodynamic interactions with the platelet inhibitors, ticlopidine and abciximab.', 'DDInter', 'Until further data are available, the coadministration of bivalirudin and antiplatelet agents, and possibly other agents with antiplatelet activity such as nonsteroidal anti-inflammatory drugs (NSAIDs), should probably be undertaken with caution. Close clinical and laboratory observation for bleeding complications is recommended.', 'Synergism', 'Until further data are available, the coadministration of bivalirudin and antiplatelet agents, and possibly other agents with antiplatelet activity such as nonsteroidal anti-inflammatory drugs (NSAIDs), should probably be undertaken with caution.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45270/', '', 'Protein C, Acenocoumarol, Selexipag', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Rabeprazole, Misoprostol, Chondroitin sulfate, More', 1767369485);
