INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (45413, 'Human adenovirus e serotype 4 strain cl-68578 antigen', 'Blinatumomab', 'Moderate', 'There are no data regarding the use of live, unattenuated Adenovirus Type 4 and Type 7 vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', 'DDInter', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies. Caution is advised.', 'Others', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45483/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (45414, 'Aminophylline', 'Blinatumomab', 'Moderate', 'Coadministration with blinatumomab may increase the plasma concentrations of drugs that are substrates of CYP450 isoenzymes. Initiation of blinatumomab treatment causes transient release of cytokines that may suppress CYP450 enzymes, although the potential for interaction has not been studied.', 'DDInter', 'Caution is advised when blinatumomab is prescribed to patients receiving drugs that are metabolized by CYP450 isoenzymes, particularly those with a narrow therapeutic index such as carbamazepine, colchicine, cyclosporine, disopyramide, phenytoin, quinidine, theophylline, warfarin, macrolide immunosuppressants, vinca alkaloids, and some narcotic analgesics. Clinical and laboratory monitoring are recommended following the initiation of blinatumomab, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', 'Caution is advised when blinatumomab is prescribed to patients receiving drugs that are metabolized by CYP450 isoenzymes, particularly those with a narrow therapeutic index such as carbamazepine, colchicine, cyclosporine, disopyramide, phenytoin, quinidine, theophylline, warfarin, macrolide immunosuppressants, vinca alkaloids, and some narcotic analgesics.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45484/', '', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Montelukast, Mepolizumab, Pentoxifylline, Zafirlukast, Bromotheophylline', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
