INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (45481, 'Blinatumomab', 'Oliceridine', 'Moderate', 'Coadministration with blinatumomab may increase the plasma concentrations of drugs that are substrates of CYP450 isoenzymes. Initiation of blinatumomab treatment causes transient release of cytokines that may suppress CYP450 enzymes, although the potential for interaction has not been studied.', 'DDInter', 'Caution is advised when blinatumomab is prescribed to patients receiving drugs that are metabolized by CYP450 isoenzymes, particularly those with a narrow therapeutic index such as carbamazepine, colchicine, cyclosporine, disopyramide, phenytoin, quinidine, theophylline, warfarin, macrolide immunosuppressants, vinca alkaloids, and some narcotic analgesics. Clinical and laboratory monitoring are recommended following the initiation of blinatumomab, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', 'Caution is advised when blinatumomab is prescribed to patients receiving drugs that are metabolized by CYP450 isoenzymes, particularly those with a narrow therapeutic index such as carbamazepine, colchicine, cyclosporine, disopyramide, phenytoin, quinidine, theophylline, warfarin, macrolide immunosuppressants, vinca alkaloids, and some narcotic analgesics.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45551/', '', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 'Oxymorphone, Pentazocine, Buprenorphine, Morphine, Butorphanol, Ibuprofen, Codeine, Nalbuphine, Acetaminophen, Dihydrocodeine, Naloxone, More', 1767369485);
