INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (46097, 'Bromocriptine', 'Bosentan', 'Moderate', 'Coadministration with bosentan may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C9 and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of those isoenzymes by bosentan.', 'DDInter', 'When drugs that are known substrates of CYP450 2C9 and/or 3A4 are coadministered with bosentan, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever bosentan is added to or withdrawn from therapy.', 'Metabolism', 'When drugs that are known substrates of CYP450 2C9 and/or 3A4 are coadministered with bosentan, the possibility of a diminished therapeutic response to those drugs should be considered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46167/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, More', 'Apomorphine, Ropinirole, Opicapone, Cabergoline, Amantadine, Rasagiline, Entacapone, Selegiline, Tolcapone, Rotigotine, Pergolide, More', 1767369485);
