INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (46298, 'Bosentan', 'Palbociclib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of palbociclib, which is a substrate of the isoenzyme.', 'DDInter', 'The potential for diminished therapeutic effects of palbociclib should be considered when prescribed with inducers of CYP450 3A4. Pharmacologic effects of palbociclib should be monitored more closely whenever an inducer is added to or withdrawn from therapy, and alternative treatment considered if an interaction is suspected.', 'Metabolism', 'The potential for diminished therapeutic effects of palbociclib should be considered when prescribed with inducers of CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46368/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, More', 'Regorafenib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Alectinib, Binimetinib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (46299, 'Bosentan', 'Panobinostat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of panobinostat, which is partially metabolized by the isoenzyme. The interaction has not been evaluated clinically, but simulations using physiologically-based pharmacokinetic (PBPK) models suggest an approximately 70% decrease in panobinostat systemic exposure in the presence of potent CYP450 3A4 inducers.', 'DDInter', 'Caution is advised during concomitant use of panobinostat with CYP450 3A4 inducers. The potential for decreased efficacy of panobinostat should be considered.', 'Metabolism', 'Caution is advised during concomitant use of panobinostat with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46369/', '', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Clindamycin, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Porfimer sodium, Niraparib, Selinexor, Aflibercept, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (46300, 'Bosentan', 'Pazopanib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of pazopanib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'The use of pazopanib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided. Alternative treatment lacking CYP450 3A4-inducing activity should be considered in patients receiving pazopanib; otherwise, pazopanib should not be used. Other known CYP450 3A4 inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they may interact with pazopanib is unknown.', 'Metabolism', 'The use of pazopanib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46370/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Clindamycin, More', 'Regorafenib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Alectinib, Binimetinib', 1767369485);
