INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (46352, 'Bosentan', 'Simvastatin', 'Moderate', 'Coadministration with bosentan may decrease the plasma concentrations of simvastatin and other HMG-CoA reductase inhibitors that are metabolized by the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of the isoenzyme by bosentan.', 'DDInter', 'The possibility of diminished HMG-CoA reductase inhibitory activity should be considered during concomitant use of atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with bosentan. Cholesterol levels should be checked following initiation or discontinuation of bosentan therapy, and the dosage of HMG-CoA reductase inhibitor adjusted accordingly. Pravastatin and fluvastatin may be reasonable alternatives, since they are metabolized by other hepatic enzymes.', 'Metabolism', 'The possibility of diminished HMG-CoA reductase inhibitory activity should be considered during concomitant use of atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with bosentan.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46422/', '', 'Ascorbic acid, Hydroflumethiazide, Lactic acid, Mitapivat, Methazolamide, Trichlormethiazide, Vemurafenib, Tioconazole, Furazolidone, Benzthiazide, Sulfamethizole, More', 'Lixisenatide, Albiglutide, Guar gum, Pramlintide, Sitagliptin, Alogliptin, Liraglutide, Semaglutide, Dulaglutide, Exenatide, Miglitol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (46353, 'Bosentan', 'Siponimod', 'Major', 'Coadministration with drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 may decrease the plasma concentrations of siponimod, which is primarily metabolized by these isoenzymes. This interaction includes concomitant use of siponimod with a moderate CYP450 2C9/strong CYP450 3A4 dual inducer or a moderate CYP450 2C9 inducer in combination with a separate strong CYP450 3A4 inducer.', 'DDInter', 'Concomitant use of siponimod and drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 (e.g., carbamazepine, enzalutamide, rifampin) is not recommended for all patients. Concomitant use of siponimod and moderate or strong CYP450 3A4 inducers (e.g., apalutamide, bosentan, dabrafenib, dexamethasone, efavirenz, eslicarbazepine, etravirine, fosphenytoin, lorlatinib, lumacaftor, mitotane, modafinil, nafcillin, nevirapine, phenobarbital, phenytoin, primidone, rifabutin, rifapentine, St. John''s wort) is not recommended for patients with CYP450 2C9*1/*3 and *2/*3 genotypes.', 'Metabolism', 'Concomitant use of siponimod and drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46423/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
