INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (47563, 'Dopamine', 'Brexpiprazole', 'Moderate', 'Phenothiazines and other neuroleptics may inhibit or reverse the pressor effect of adrenaline (epinephrine), dopamine, and similar vasoconstrictors. Many of these agents, including the atypical antipsychotics, exhibit alpha-1 adrenergic blocking activity and produce hypotension as an adverse effect. Use of adrenaline or dopamine for drug-induced hypotension and circulatory collapse in patients receiving neuroleptic therapy may cause a paradoxical further lowering of blood pressure, since beta stimulation may worsen hypotension in the setting of alpha blockade.', 'DDInter', 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents. Alternative vasopressor agents such as metaraminol, noradrenaline (norepinephrine), or phenylephrine should be considered, and vital signs closely monitored.', 'Antagonism', 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/47633/', '', 'Dobutamine, Norepinephrine, Midodrine, Amrinone, Droxidopa, Angiotensin II, Ephedrine, Milrinone, Arbutamine, Phenylephrine, Metaraminol, More', 'Cariprazine, Droperidol, Pimavanserin', 1767369485);
