INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (47597, 'Ezogabine', 'Brexpiprazole', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/47667/', '', 'Lacosamide', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (47598, 'Fedratinib', 'Brexpiprazole', 'Major', 'Coadministration with inhibitors of CYP450 3A4 and/or 2D6 may significantly increase the plasma concentrations of brexpiprazole, which is metabolized by these isoenzymes.', 'DDInter', 'The manufacturer recommends that the brexpiprazole dosage be reduced as follows during concomitant administration of CYP450 inhibitors. The brexpiprazole dosage should be increased to the original level if these agents are discontinued. It is advisable to monitor patients for clinical response.', 'Metabolism', 'The manufacturer recommends that the brexpiprazole dosage be reduced as follows during concomitant administration of CYP450 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/47668/', '', 'Asenapine, Promazine, Prochlorperazine, Thiothixene, Olanzapine, Trifluoperazine, Amisulpride, Triflupromazine, Loxapine, Molindone, Droperidol, Paliperidone', 'Entrectinib, Acalabrutinib, Tepotinib, Avapritinib, Bosutinib, Lapatinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, More', 1767369485);
