INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (47673, 'Brexpiprazole', 'Levocetirizine', 'Moderate', 'Concurrent use of cetirizine or levocetirizine with alcohol or other agents that exhibit central nervous system (CNS) depressant effects may result in additive impairment of mental alertness and performance.', 'DDInter', 'Concomitant use of cetirizine or levocetirizine with alcohol or other CNS depressants should generally be avoided if possible. In the event that they are used together, patients should be counseled against driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'Concomitant use of cetirizine or levocetirizine with alcohol or other CNS depressants should generally be avoided if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/47743/', '', 'Ketotifen, Fexofenadine, Loratadine, Desloratadine, Azelastine, Terfenadine, Astemizole', 'Amisulpride, Pimavanserin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (47674, 'Brexpiprazole', 'Levodopa', 'Moderate', 'Agents with central antidopaminergic activity such as phenothiazines, neuroleptics, and certain antiemetics may antagonize the pharmacologic effects of dopaminergic drugs, and vice versa. In addition, the central nervous system depressant and hypotensive effects of these agents may be additively or synergistically increased when taken together.', 'DDInter', 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided. If coadministration is necessary, patients should be alerted to the possibility of excessive drowsiness and monitored for potentially diminished therapeutic response to both treatments. Patients treated for Parkinson''s disease should generally avoid antidopaminergic agents, particularly phenothiazines and older neuroleptic agents, since these agents may cause extrapyramidal reactions and exacerbate the symptoms of Parkinson''s disease. Likewise, patients with a major psychotic disorder should ordinarily not be treated with dopaminergic drugs because of the risk of exacerbating the psychosis with an increase in central dopaminergic tone.', 'Synergism', 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/47744/', '', 'Pimavanserin', '', 1767369485);
