INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (48583, 'Brimonidine (ophthalmic)', 'Tizanidine', 'Moderate', 'Topically administered alpha-2 adrenergic agonists such as apraclonidine and brimonidine may potentiate the hypotensive and sedative effects of tizanidine, which is a muscle relaxant with centrally-acting alpha-2 adrenergic activity. Pharmacologic studies have found tizanidine to possess between 1/10 to 1/50 of the potency of clonidine, a structurally similar agent, in lowering blood pressure.', 'DDInter', 'Caution is advised if tizanidine is used in combination with topical alpha-2 adrenergic agonists. Close monitoring for development of hypotension is recommended. Patients should be advised to avoid rising abruptly from a sitting or recumbent position, and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is advised if tizanidine is used in combination with topical alpha-2 adrenergic agonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48653/', '', 'Deoxycholic acid, Calcium gluconate, Pimecrolimus, Magnesium sulfate, Glycopyrronium, Aminobenzoic acid, Ivermectin, Cromoglicic acid, Abrocitinib, Dupilumab, Tralokinumab, More', '', 1767369485);
