INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (48716, 'Timolol (ophthalmic)', 'Methacholine', 'Major', 'Patients who are concurrently receiving beta blockers may have exaggerated or prolonged responses (i.e., bronchoconstriction) to methacholine challenge and may not respond readily to treatment. Beta-2-adrenergic blockade increases airway resistance. Ophthalmically applied beta blockers undergo some systemic absorption and potentially may also interact.', 'DDInter', 'Administration of methacholine to a patient on any beta blocker therapy is considered contraindicated.', 'Others', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48787/', '', 'Nebivolol, Carteolol, Latanoprost, Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Clonidine, Physostigmine, Acetylcholine, Diclofenamide, More', 'Sorbitol, Caffeine, Edrophonium, Indocyanine green, Inulin, Hexaminolevulinate, Secretin human, Galactose, Corticorelin, Macimorelin, Folic acid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (48717, 'Timolol (ophthalmic)', 'Methadone', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 may increase the systemic effects of topically administered timolol, which is metabolized by the isoenzyme. Following ocular administration, timolol is systemically absorbed and can reach plasma levels associated with adverse beta-adrenergic blocking effects such as bronchospasm, depression, bradycardia, and hypotension. The risk may be increased if clearance of the drug is significantly diminished by concomitant CYP450 2D6 inhibitors.', 'DDInter', 'Patients should be monitored for systemic beta-adrenergic blocking effects of topical timolol during coadministration with CYP450 2D6 inhibitors such as cimetidine, quinidine, and certain selective serotonin reuptake inhibitors. Particular caution is warranted in elderly patients, since they are generally more susceptible to adverse effects of topically administered beta blockers.', 'Metabolism', 'Patients should be monitored for systemic beta-adrenergic blocking effects of topical timolol during coadministration with CYP450 2D6 inhibitors such as cimetidine, quinidine, and certain selective serotonin reuptake inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48788/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Levacetylmethadol, Nalmefene, Nicotine, Buprenorphine, Naltrexone, More', 'Penbutolol, Carteolol, Latanoprost, Methazolamide, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, More', 1767369485);
