INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (50020, 'Diflunisal', 'Budesonide', 'Moderate', 'The combined use of corticosteroids and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', 'DDInter', 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be considered.', 'Synergism', 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50091/', '', 'Ziconotide, Methoxyflurane', 'Formoterol, Cromoglicic acid, Ipratropium, Isoprenaline, Tiotropium, Beclomethasone dipropionate, Terbutaline, Aclidinium, Glycopyrronium, Mometasone, Fluticasone, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (50021, 'Diltiazem', 'Budesonide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the systemic bioavailability of budesonide, which undergoes extensive first-pass and systemic metabolism via intestinal and hepatic CYP450 3A4. In pharmacokinetic studies, 6- to 8-fold increases in budesonide systemic exposure (AUC) have been observed during coadministration of the potent CYP450 3A4 inhibitor ketoconazole with different oral formulations of budesonide.', 'DDInter', 'The possibility of increased systemic adverse effects of budesonide should be considered during coadministration with CYP450 3A4 inhibitors. If concomitant use cannot be avoided, the dosing times between budesonide and the CYP450 3A4 inhibitor should be separated by as much as possible. In addition, the lowest effective dosage of budesonide should be prescribed, and further adjustments made as necessary according to therapeutic response and tolerance.', 'Metabolism', 'The possibility of increased systemic adverse effects of budesonide should be considered during coadministration with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50092/', '', 'Tetracaine, Fluocinolone acetonide, Procaine, Betamethasone, Fluorometholone', 'Mometasone furoate, Terbutaline, Umeclidinium, Aclidinium, Glycopyrronium, Mometasone, Ipratropium, Orciprenaline, Bitolterol, Olodaterol, Isoprenaline, More', 1767369485);
