INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (50501, 'Chlorthalidone', 'Bumetanide', 'Moderate', 'The combination of a thiazide and loop diuretic may produce additive or synergistic effects on diuresis and excretion of electrolytes including sodium, potassium, magnesium, and chloride. Although these agents may be combined therapeutically in some patients with inadequate response to a single agent, the increased risk of dehydration, hypotension, hypokalemia, hypomagnesemia, and hyponatremia should be recognized.', 'DDInter', 'Caution is advised during concomitant use of a thiazide and loop diuretic. Dosages should be titrated slowly and carefully, and electrolytes, BUN, fluid status, blood pressure, and renal function should be monitored regularly. Patients should be advised to contact their physician if they experience signs and symptoms of fluid and electrolyte depletion such as dizziness, lightheadedness, dry mouth, thirst, fatigue, weakness, lethargy, muscle cramps, decreased urination, postural hypotension, and tachycardia.', 'Synergism', 'Caution is advised during concomitant use of a thiazide and loop diuretic.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50572/', '', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Ciclopirox, Vemurafenib, Darunavir, More', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (50502, 'Cholestyramine', 'Bumetanide', 'Moderate', 'INTERVAL: Limited data have shown that the bioavailability of orally administered furosemide is reduced by 95% during concurrent administration of cholestyramine and by 80% during concurrent administration of colestipol. (These figures are based on data from the area under the plasma concentration-time curve and total urinary elimination data.) These decreases in bioavailability were associated with 77% and 58% reductions in the diuretic response to furosemide. No data are available for bumetanide, torsemide, or ethacrynic acid, but a similar interaction is suspected.', 'DDInter', 'Diuretics should be administered at least 2 to 3 hours before cholestyramine or colestipol. If alternating dosage schedules is ineffective, it may be necessary to increase diuretic dosage during coadministration. Patients should be monitored for altered diuretic efficacy.', 'Absorption', 'Diuretics should be administered at least 2 to 3 hours before cholestyramine or colestipol.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50573/', '', 'Tamsulosin, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Ciclopirox, More', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Cerivastatin, Lomitapide, Bempedoic acid, More', 1767369485);
