INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51232, 'Buprenorphine', 'Propafenone', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51303/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Oxymorphone, Tapentadol, Oxycodone, More', 'Moricizine, Tocainide, Bretylium, Mexiletine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51233, 'Buprenorphine', 'Propantheline', 'Moderate', 'Coadministration of opioids with anticholinergic agents may result in additive central nervous system (CNS), gastrointestinal, and genitourinary effects. The risk and/or severity of adverse effects such as sedation, dizziness, confusion, cognitive and psychomotor impairment, dry mouth, constipation, and urinary retention may increase. Severe constipation may lead to paralytic ileus in some cases.', 'DDInter', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51304/', '', 'Alosetron, Isometheptene', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Ibuprofen, Celecoxib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51234, 'Buprenorphine', 'Propiomazine', 'Major', 'Concomitant use of buprenorphine with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may increase the risk of buprenorphine overdose, severe respiratory depression, coma, and death.', 'DDInter', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them. Extreme caution is advised when prescribing buprenorphine to patients who are addicted to opioids and also abusing benzodiazepines or alcohol. Due to potential risk of overdose and death, dependence on sedative-hypnotics such as benzodiazepines or alcohol is considered a relative contraindication for office-based buprenorphine treatment of opioid addiction.', 'Synergism', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51305/', '', 'Zopiclone', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Levacetylmethadol, Nalmefene, Nicotine, Naltrexone, Acetaminophen, Dezocine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51235, 'Dextropropoxyphene', 'Buprenorphine', 'Major', 'Sedatives, tranquilizers, muscle relaxants, antidepressants, and other central nervous system (CNS) depressants may have additive CNS- and/or respiratory-depressant effects with propoxyphene. Misuse of propoxyphene, either alone or in combination with other CNS depressants, has been a major cause of drug-related deaths, particularly in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. Pharmacokinetically, propoxyphene is an inhibitor of CYP450 2D6 and may increase the plasma concentrations of many psychotropic agents that are metabolized by the isoenzyme such as phenothiazines, haloperidol, risperidone, phenobarbital, and some tricyclic antidepressants and serotonin reuptake inhibitors.', 'DDInter', 'Caution is advised if propoxyphene is used with sedatives, tranquilizers, muscle relaxants, antidepressants, and other CNS depressants, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. Dosage reductions may be appropriate. Patients should be monitored for potentially excessive or prolonged CNS and respiratory depression and other CNS adverse effects. Patients should be warned not to exceed recommended dosages, to avoid alcohol, and to avoid activities requiring mental alertness until they know how these agents affect them.', 'Metabolism, Synergism', 'Caution is advised if propoxyphene is used with sedatives, tranquilizers, muscle relaxants, antidepressants, and other CNS depressants, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51306/', '', 'Buprenorphine', 'Acamprosate, Varenicline, Disulfiram, Nalmefene, Nicotine, Acetaminophen, Dextropropoxyphene, Ibuprofen, Acetylsalicylic acid, Naloxone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51236, 'Buprenorphine', 'Propranolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51307/', '', 'Penbutolol, Carteolol', 'Nalmefene, Disulfiram, Levacetylmethadol, Naloxone, Tapentadol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51237, 'Buprenorphine', 'Protriptyline', 'Moderate', 'Opioids may potentiate the effects of serotonergic agents and increase the risk of serotonin syndrome. Serotonin syndrome is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea. Since many serotonergic agents can also cause central nervous system depression, concomitant use with opioids may result in increased sedation and impairment of judgment, thinking, and psychomotor skills.', 'DDInter', 'Caution is advised when opioids are used concomitantly with serotonergic agents such as selective serotonin reuptake inhibitors (SSRIs), serotonin and norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), other antidepressants/psychotropic agents (e.g., amoxapine, buspirone, lithium, maprotiline, mirtazepine, nefazodone, trazodone, vilazodone), 5-HT1 receptor agonists (triptans), 5-HT3 receptor antagonists, cyclobenzaprine, dextromethorphan, 5-hydroxytryptophan, and St. John''s wort. Patients should be monitored for symptoms of the serotonin syndrome during treatment. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary.', 'Synergism', 'Caution is advised when opioids are used concomitantly with serotonergic agents such as selective serotonin reuptake inhibitors (SSRIs), serotonin and norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), other antidepressants/psychotropic agents (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51308/', '', 'Acamprosate, Naltrexone, Varenicline, Nalmefene, Nicotine, Naltrexone, Acetaminophen, Ibuprofen, Acetylsalicylic acid, Naloxone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51238, 'Buprenorphine', 'Mepyramine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51309/', '', 'Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Tetracaine, Ketotifen, Fexofenadine, Loratadine, Desloratadine, Azelastine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51239, 'Buprenorphine', 'Quazepam', 'Major', 'Concomitant use of buprenorphine with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may increase the risk of buprenorphine overdose, severe respiratory depression, coma, and death.', 'DDInter', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them. Extreme caution is advised when prescribing buprenorphine to patients who are addicted to opioids and also abusing benzodiazepines or alcohol. Due to potential risk of overdose and death, dependence on sedative-hypnotics such as benzodiazepines or alcohol is considered a relative contraindication for office-based buprenorphine treatment of opioid addiction.', 'Synergism', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51310/', '', 'Acamprosate, Naltrexone, Varenicline, Nalmefene, Nicotine, Acetylsalicylic acid, Naloxone, Naltrexone, Celecoxib', 'Zopiclone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51240, 'Buprenorphine', 'Quetiapine', 'Moderate', 'There is some concern that quetiapine may have additive cardiovascular effects in combination with other drugs that are known to prolong the QT interval of the electrocardiogram.', 'DDInter', 'Coadministration of quetiapine with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. In addition, if combination therapy with agents with anticholinergic properties is required, caution is advised, particularly in the elderly and those with underlying organic brain disease.', 'Synergism', 'Coadministration of quetiapine with other drugs that can prolong the QT interval should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51311/', '', 'Nalmefene, Disulfiram, Naloxone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51241, 'Buprenorphine', 'Quinapril', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51312/', '', 'Nalmefene, Disulfiram, Levacetylmethadol, Acamprosate, Tapentadol, Naloxone, Dezocine, Oxymorphone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51242, 'Buprenorphine', 'Quinidine', 'Moderate', 'Buprenorphine administered transdermally at a higher than recommended dosage has been associated with prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In general, the risk of an individual agent or a combination of agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances (e.g., hypokalemia, hypomagnesemia). In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 'According to the product labeling, use of buprenorphine transdermal films should be avoided in patients treated with class IA (e.g., disopyramide, quinidine, procainamide) or class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents. A dosage of 20 mcg/hr should not be exceeded in patients receiving the buprenorphine transdermal system. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'According to the product labeling, use of buprenorphine transdermal films should be avoided in patients treated with class IA (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51313/', '', 'Moricizine, Tocainide, Bretylium, Mexiletine', 'Diamorphine, Acamprosate, Naltrexone, Disulfiram, Nalmefene, Naltrexone, Oxymorphone, Tapentadol, Butorphanol, Dezocine, Opium, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51243, 'Buprenorphine', 'Quinine', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51314/', '', 'Risdiplam, Collagenase clostridium histolyticum, Onasemnogene abeparvovec, Hyaluronic acid, Artesunate, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Proguanil, Sulfadoxine', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Oxymorphone, Tapentadol, Oxycodone, More', 1767369485);
