INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51268, 'Buprenorphine', 'Saquinavir', 'Major', 'Saquinavir in combination with ritonavir may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of ritonavir-boosted saquinavir with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51339/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Codeine, Naltrexone, Oxymorphone, Tapentadol, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, More', 1767369485);
