INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (51824, 'Itraconazole', 'Busulfan', 'Moderate', 'Itraconazole has been reported to inhibit the clearance of busulfan. The mechanism of interaction has not been established. Itraconazole is a potent inhibitor of the CYP450 3A4 isoenzyme and P-glycoprotein (P-gp) efflux transporter, although there is little evidence to suggest that busulfan is a substrate of either.', 'DDInter', 'Patients prescribed busulfan with itraconazole or ketoconazole should be monitored for potentially increased myelotoxic, hepatotoxic, neurotoxic, and gastrointestinal toxic effects. Some experts also recommend the same precaution during coadministration with posaconazole or voriconazole, although clinical data are lacking.', 'Metabolism, Excretion', 'Patients prescribed busulfan with itraconazole or ketoconazole should be monitored for potentially increased myelotoxic, hepatotoxic, neurotoxic, and gastrointestinal toxic effects.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51895/', '', 'Temozolomide, Streptozocin, Chlorambucil, Lomustine, Dacarbazine, Mechlorethamine, Bendamustine, Melphalan flufenamide, Uracil mustard', 'Flucytosine, Anidulafungin, Micafungin, Oteseconazole', 1767369485);
