INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (52685, 'Cabazitaxel', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cabazitaxel, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if cabazitaxel is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to cabazitaxel should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the cabazitaxel dosage adjusted as necessary. Regular monitoring of peripheral blood counts between treatment cycles is recommended, and patients should be advised to contact their physician if they experience potential signs and symptoms of cabazitaxel toxicity such as infection, fever, chills, diarrhea, unusual bruising or bleeding, and burning, tingling, or numbness in the hands and feet.', 'Metabolism', 'Caution is advised if cabazitaxel is prescribed with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/52756/', '', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, More', 'Topotecan, Teniposide, Etoposide', 1767369485);
