INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (52956, 'Colchicine', 'Cabozantinib', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may significantly increase the serum concentrations of colchicine. The mechanism involves enhanced absorption as well as reduced excretion of colchicine due to inhibition of P-gp efflux transporter in the intestine, renal proximal tubule, and liver.', 'DDInter', 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors. For the treatment of acute gout flares, the adjusted dosage recommended is 0.6 mg for one dose. Administration should not be repeated for at least three days.', 'Excretion', 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53027/', '', 'Pegloticase, Febuxostat, Allopurinol, Rasburicase', 'Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Afatinib, Tivozanib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (52957, 'Conivaptan', 'Cabozantinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme. High plasma levels of cabozantinib may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Concomitant use of cabozantinib with potent CYP450 3A4 inhibitors should generally be avoided. If concomitant use is required, the daily dosage of cabozantinib should be reduced by 40 mg (e.g., 140 mg to 100 mg daily; 100 mg to 60 mg daily). The original dosage may be resumed 2 to 3 days after discontinuation of the potent CYP450 3A4 inhibitor. However, some authorities recommend avoiding concomitant use of cabozantinib during and for 2 weeks after treatment with itraconazole. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Patients should also be advised regarding a potential increase in more common side effects of cabozantinib, such as diarrhea, fatigue, nausea, decreased appetite, palmar-plantar erythrodysesthesia syndrome (PPES), hypertension, vomiting, weight loss, and constipation.', 'Metabolism', 'Concomitant use of cabozantinib with potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53028/', '', 'Alpelisib, Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', '', 1767369485);
