INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (53095, 'Cabozantinib', 'Macimorelin', 'Major', 'Macimorelin can cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of macimorelin with other drugs that can prolong the QT interval should generally be avoided. A sufficient washout period following discontinuation of these drugs is recommended prior to macimorelin administration. Patients treated with any medication that can cause QT prolongation should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of macimorelin with other drugs that can prolong the QT interval should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53166/', '', 'Imatinib, Acalabrutinib, Dacomitinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, More', 'Sorbitol, Caffeine, Edrophonium, Indocyanine green, Tolbutamide, Inulin, Hexaminolevulinate, Secretin human, Galactose, Corticorelin, Methacholine, More', 1767369485);
