INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (53302, 'Caffeine', 'Methotrexate', 'Moderate', 'Limited data suggest that consumption of greater than 180 mg/day of caffeine may interfere with the efficacy of methotrexate (MTX) in patients with rheumatoid arthritis. The exact mechanism of interaction is unknown but may be related to the antagonistic effect of caffeine on adenosine receptors, as anti-inflammatory properties of MTX is thought to result from the accumulation of adenosine.', 'DDInter', 'Until further information is available, the potential for interaction should be considered in patients who consume substantial amounts of caffeine and caffeine-containing foods and are prescribed methotrexate for rheumatoid arthritis. It may be appropriate to limit caffeine intake if an interaction is suspected in cases of treatment failure.', 'Antagonism', 'Until further information is available, the potential for interaction should be considered in patients who consume substantial amounts of caffeine and caffeine-containing foods and are prescribed methotrexate for rheumatoid arthritis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53373/', '', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Aminobenzoic acid, Ivermectin, Abrocitinib, Dupilumab, Tralokinumab, Povidone-iodine, More', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 1767369485);
