INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (53683, 'Digitoxin', 'Calcitriol', 'Moderate', 'Excessive dosing of vitamin D can induce hypercalcemia, which may increase the risk of digitalis toxicity and serious arrhythmias due to additive inotropic effects.', 'DDInter', 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs. Serum digoxin, calcium, and phosphorus levels should be closely monitored, especially early in treatment during dosage adjustment. The serum calcium X phosphorus product should be maintained at less than 55 in patients with chronic kidney disease and should not be allowed to exceed 70 in any patient. An estimate of daily dietary calcium intake should be made and the intake adjusted when indicated. Patients should be advised to avoid an abrupt increase in dietary calcium intake, as it may trigger hypercalcemia, and to contact their physician if they experience early symptoms of vitamin D intoxication associated with hypercalcemia such as weakness, fatigue, headache, somnolence, vertigo, tinnitus, anorexia, nausea, vomiting, constipation, dry mouth, metallic taste, muscle pain, bone pain, ataxia, and hypotonia.', 'Synergism', 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53754/', '', 'Tazarotene, Methoxsalen, Calcipotriol, Trioxsalen, Vitamin A, Beta carotene, Dihydrotachysterol', '', 1767369485);
