INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (53833, 'Diclofenac', 'Artesunate', 'Moderate', 'Coadministration with potent inhibitors of uridine diphosphate glucuronosyltransferase (UGT) may increase the peak plasma concentration (Cmax) and systemic exposure (AUC) of dihydroartemisinin (DHA), an active metabolite of artesunate and substrate of UGT 1A9 or 2B7. Increased exposure to artesunate may increase the risk of adverse effects such as anemia, increased liver transaminases, thrombocytopenia, hyperbilirubinemia, acute renal failure, and leukocytosis.', 'DDInter', 'Monitoring for adverse reactions is recommended if artesunate is coadministered with potent UGT inhibitors. Dosage adjustments or alternative treatments may be required based on clinical response and tolerance.', 'Others', 'Monitoring for adverse reactions is recommended if artesunate is coadministered with potent UGT inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53904/', '', 'Fludrocortisone, Indomethacin, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Salicylic acid, Ketorolac, More', 'Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Halofantrine, Quinine, Primaquine, Proguanil, Sulfadoxine, Chloroquine', 1767369485);
