INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (53868, 'Diclofenac', 'Daptomycin', 'Moderate', 'The concomitant administration of daptomycin and other nephrotoxic drugs or drugs that reduce renal filtration may result in additive renal effects and/or increased plasma levels of daptomycin. Daptomycin has been associated with renal impairment, although causality has not been determined. Increased plasma concentrations of daptomycin may increase the risk of myopathy. The risk may be increased in patients with pre-existing renal dysfunction.', 'DDInter', 'Serum drug concentrations and renal function, including serum creatinine, should be carefully monitored during concurrent administration. In addition, all patients receiving daptomycin should be monitored for the development of muscle pain or weakness, particularly of the distal extremities. CPK levels should be monitored weekly, and patients who develop unexplained elevations in CPK should be monitored more frequently. Daptomycin should be discontinued in patients with unexplained signs and symptoms of myopathy in conjunction with CPK levels exceeding 1000 U/L (approximately 5 times the upper limit of normal), or in patients without reported symptoms who have marked elevations in CPK (10 times the upper limit of normal, or more).', 'Synergism', 'Serum drug concentrations and renal function, including serum creatinine, should be carefully monitored during concurrent administration.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53939/', '', 'Tinidazole, Bacitracin, Polymyxin B, Oritavancin, Telavancin, Methenamine, Spectinomycin, Fosfomycin, Tedizolid, Dalbavancin', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methyl